// Local loader for jsPDF AutoTable plugin
(function(){
  if(window.jspdf && window.jspdf.autoTable) return;
  var url = 'https://cdnjs.cloudflare.com/ajax/libs/jspdf-autotable/3.5.25/jspdf.plugin.autotable.min.js';
  var s = document.createElement('script'); s.src = url; s.async = false;
  s.onload = function(){ console.info('jsPDF AutoTable loaded from CDN'); };
  s.onerror = function(){ console.error('Failed to load jsPDF AutoTable from CDN'); };
  document.head.appendChild(s);
})();
