// Local loader for jsPDF (UMD)
(function(){
  if(window.jspdf && window.jspdf.jsPDF) return;
  var url = 'https://cdnjs.cloudflare.com/ajax/libs/jspdf/2.5.1/jspdf.umd.min.js';
  var s = document.createElement('script'); s.src = url; s.async = false;
  s.onload = function(){ console.info('jsPDF loaded from CDN'); };
  s.onerror = function(){ console.error('Failed to load jsPDF from CDN'); };
  document.head.appendChild(s);
})();
