// Local loader for SheetJS (xlsx)
// If real local minified file is present, keep it here. This loader will dynamically load upstream CDN if needed.
(function(){
  if(window.XLSX) return;
  var url = 'https://cdn.jsdelivr.net/npm/xlsx@0.18.5/dist/xlsx.full.min.js';
  var s = document.createElement('script'); s.src = url; s.async = false;
  s.onload = function(){ console.info('SheetJS loaded from CDN'); };
  s.onerror = function(){ console.error('Failed to load SheetJS from CDN'); };
  document.head.appendChild(s);
})();
